mapper.select:
SELECT a.id, a.title, a.summary, a.content, c.nick_name AS authorName, b.name AS categoryName, a.created_at AS createdAt, a.updated_at AS updatedAt FROM t_content_article a LEFT JOIN t_content_category b ON a.category_id = b.id LEFT JOIN t_sys_user c ON a.author_id = c.id
a.is_deleted = 0
AND a.title LIKE CONCAT('%', #{req.title}, '%')
AND a.status = #{req.status}
AND a.category_id = #{req.categoryId}
ORDER BY a.created_at DESC LIMIT #{offset}, #{size}

SELECT COUNT(a.id) FROM t_content_article a LEFT JOIN t_content_category b ON a.category_id = b.id LEFT JOIN t_sys_user c ON a.author_id = c.id
a.is_deleted = 0
AND a.title LIKE CONCAT('%', #{req.title}, '%')
AND a.status = #{req.status}
AND a.category_id = #{req.categoryId}
